import React, { useState, useRef, useEffect } from "react";
import { IoIosSend } from "react-icons/io";
import { useSelector } from "react-redux";
import { RootState } from "../../Redux/store";
import { sendChatApi } from "../../Api/Chat/sendChatApi";
import { fetchMessageApi } from "../../Api/Chat/fetchMessage";
import { io } from "socket.io-client";
import { SOCKET_URL } from "../../config/sokect";
interface Conversation {
  sender: string;
  message: string;
}

export default function Chat() {
  const authData = useSelector((state: RootState) => state.authData.authData);
  const [currentChat, setcurrentChat] = useState<string>();
  const [arrivalMessage, setArrivalMessage] = useState<null | any>(null);
  const [conversationsData, setConversations] = useState<Conversation[]>([]);

  const scrollref = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const socket = useRef<HTMLDivElement | any>();

  useEffect(() => {
    if (authData) {
      socket.current = io(SOCKET_URL);
      socket.current?.on("getMessage", ({ senderId, message, sender }: any) => {
        setArrivalMessage({
          senderId,
          message,
          sender,
          date: Date.now(),
        });
      });
    }
  }, [authData]);

  useEffect(() => {
    authData &&
      socket.current.emit("addUsers", {
        userId: authData._id,
        role: "user",
      });
  }, [authData]);

  useEffect(() => {
    if (authData) {
      const getMessage = async () => {
        try {
          await fetchMessageApi(authData._id).then((res) => {
            // setArrivalMessage(res?.data.result);
            setConversations(res?.data.result.conversation);
          });
        } catch (error) {
          console.log(error);
        }
      };
      getMessage();
    }
  }, [authData]);

  const sendMessage = (e: any) => {
    e.preventDefault();

    socket.current?.emit("send-message", {
      userId: authData._id,
      message: currentChat,
      sender: "user",
    });

    sendChatApi(authData?._id, {
      sender: "user",
      message: currentChat,
    })
      .then((res) => {
        if (res?.data.success) {
          setcurrentChat("");
        }
      })
      .catch((error) => {
        setcurrentChat("");
      });
  };

  useEffect(() => {
    inputRef.current?.focus();
    scrollref.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversationsData]);

  useEffect(() => {
    arrivalMessage &&
      setConversations((prev) => [
        ...prev,
        { sender: arrivalMessage.sender, message: arrivalMessage.message },
      ]);
  }, [arrivalMessage]);

  return (
    <div>
      <body className="flex flex-col items-center justify-center w-screen min-h-screen bg-gray-100 text-gray-800 p-10">
        <div className="flex flex-col flex-grow w-full max-w-xl bg-white shadow-xl rounded-lg overflow-hidden">
          <div className="flex flex-col flex-grow h-0 p-4 overflow-auto">
            <>
              <div className="flex w-full mt-2 space-x-3 max-w-xs">
                <div className="flex-shrink-0 h-10 w-10 rounded-full">
                  <img
                    className="h-10 w-10 rounded-full"
                    src={require("../../Assets/boy.png")}
                    alt="profile_pic"
                  />
                </div>
                <div>
                  <div className=" bg-gray-300 p-3 rounded-r-lg rounded-bl-lg">
                    <p className="text-sm font-Yatra font-bold">
                      "Welcome to our Travio!! How can we assist
                      you? Type your message below and we'll get back to you as
                      soon as possible. Thank you for choosing our services!"
                    </p>
                  </div>
                  <span className="text-xs text-gray-500 leading-none"></span>
                </div>
              </div>
            </>
            {conversationsData?.map((item: any) => {
              console.log(item);

              return (
                <div
                  className={` ${
                    item.sender === "user"
                      ? "flex w-full mt-2 space-x-3 max-w-xs ml-auto justify-end"
                      : "flex w-full mt-2 space-x-3 max-w-xs"
                  }`}
                >
                  <div
                    className={`${
                      item.sender === "admin"
                        ? "flex-shrink-0 h-10 w-10 rounded-full"
                        : "flex-shrink-0 h-10 w-10 rounded-full"
                    }`}
                  >
                    <img
                      className="h-10 w-10 rounded-full"
                      src={`${
                        item.sender === "user"
                          ? "data:image/jpeg;base64,"
                          : require("../../Assets/boy.png")
                      } `}
                      alt="profile_pic"
                    />
                  </div>
                  <div>
                    <div
                      ref={scrollref}
                      className={`${
                        item.sender === "user"
                          ? "bg-blue-600 text-white p-3 rounded-l-lg rounded-br-lg"
                          : " bg-gray-300 p-3 rounded-r-lg rounded-bl-lg"
                      }`}
                    >
                      <p className="text-sm">{item.message}</p>
                    </div>
                    <span className="text-xs text-gray-500 leading-none"></span>
                  </div>
                </div>
              );
            })}
          </div>

          <form onSubmit={sendMessage}>
            <div className="bg-gray-300 p-4 flex">
              <input
                className="flex items-center h-10 w-full border-danger-300 rounded px-3 text-sm"
                type="text"
                placeholder="Type your message…"
                value={currentChat}
                ref={inputRef}
                onChange={(e) => setcurrentChat(e.target.value)}
              />

              <div>
                <button type="submit" className="text-3xl mt-1 ">
                  <IoIosSend />
                </button>
              </div>
            </div>
          </form>
        </div>
      </body>
    </div>
  );
}
